mapper.select:
select articleno, subject, userid, content, hit, regtime, reply
from board 
where articleno = #{articleno}

mapper.insert:
insert into board(
	subject, userid, content
) values (
	#{subject}, #{userid}, #{content}
)

mapper.update:
update board 
   set subject = #{subject}, 
   	   userid = #{userid}, 
   	   content = #{content}
 where articleno = #{articleno}

update board 
   set reply = #{reply}
 where articleno = #{articleno}

update board 
   set reply = NULL
 where articleno = #{articleno}

update board 
   set hit = #{hit}+1
 where articleno = #{articleno}
